import React, { Component } from 'react';
import UserLayout from "../../../hoc/userLayout";

import FormField from '../../utils/Form/FormField';
import {update, generateData, isFormValid, populateOptionFields} from '../../utils/Form/formActions';

import { connect } from 'react-redux';
import { getBrands, getWoods, addProduct } from '../../../actions/products_actions';

class AddProduct extends Component {

    state = {
        formError: false,
        formSuccess: false,
        formdata: {
            name:{
                element: 'input',
                value: '',
                config: {
                    label: 'Product Name',
                    name: 'name_input',
                    type: 'text',
                    placeholder: 'Enter your name'
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            description:{
                element: 'textarea',
                value: '',
                config: {
                    label: 'Product Description',
                    name: 'description_input',
                    type: 'text',
                    placeholder: 'Enter your description'
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            price:{
                element: 'input',
                value: '',
                config: {
                    label: 'Product Price',
                    name: 'price_input',
                    type: 'text',
                    placeholder: 'Enter your price'
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            brand:{
                element: 'select',
                value: '',
                config: {
                    label: 'Product Brand',
                    name: 'brands_input',
                    options: []
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            shipping:{
                element: 'select',
                value: '',
                config: {
                    label: 'Shipping',
                    name: 'shipping_input',
                    options: [
                        {key: true, value: 'Yes'},
                        {key: false, value: 'No'},
                    ]
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            available:{
                element: 'select',
                value: '',
                config: {
                    label: 'Available, in stock',
                    name: 'available_input',
                    options: [
                        {key: true, value: 'Yes'},
                        {key: false, value: 'No'},
                    ]
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            wood:{
                element: 'select',
                value: '',
                config: {
                    label: 'Wood Material',
                    name: 'wood_input',
                    options: []
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            frets:{
                element: 'select',
                value: '',
                config: {
                    label: 'Frets',
                    name: 'frets_input',
                    options: [
                        {key: 20, value: 20},
                        {key: 21, value: 21},
                        {key: 22, value: 22},
                        {key: 24, value: 24},
                    ]
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
            publish:{
                element: 'select',
                value: '',
                config: {
                    label: 'Publish',
                    name: 'publish_input',
                    options: [
                        {key: true, value: 'Public'},
                        {key: false, value: 'Hidden'},
                    ]
                },
                validation: {
                    required: true,
                },
                valid: false,
                touches: false,
                validationMessage: '',
                showLabel: true
            },
        }
    }

    updateFields = (newFormData) => {
        this.setState({
            formdata: newFormData
        })
    }

    updateForm = (element) => {
        const newFormdata = update(element, this.state.formdata, 'products');
        this.setState({
            formError: false,
            formdata: newFormdata
        })
    }

    resetFieldHandler = () => {
        this.setState({
            formSuccess: true
        })
    }

    submitForm = (event) => {
        event.preventDefault();

        let dataToSubmit = generateData(this.state.formdata, 'products');
        let formIsValid = isFormValid(this.state.formdata, 'products');

        if(formIsValid){
            console.log(dataToSubmit)
            this.props.dispatch(addProduct(dataToSubmit)).then(()=>{
                if(this.props.products.addProduct.success){
                    this.resetFieldHandler()
                }else{
                    this.setState({formError: true})
                }
            })
            /* this.props.dispatch(registerUser(dataToSubmit))
            .then(response =>{
                if(response.payload.success){
                    this.setState({
                        formError: false,
                        formSuccess: true
                    });
                    setTimeout(() => {
                        this.props.history.push('/register_login');
                    }, 2000)
                }else{
                    this.setState({
                        formError: true
                    })
                }
            }).catch( e => this.setState({formError: true})) */
        }else{
            this.setState({
                formError: true
            })
        }
    }

    componentDidMount(){
        const formdata = this.state.formdata;

        this.props.dispatch(getBrands())
        .then(response => {
            const newFormData = populateOptionFields(formdata, this.props.products.brands,'brand')
            this.updateFields(newFormData)
            //console.log(newFormData);
        }).catch( e => console.log(e))

        this.props.dispatch(getWoods())
        .then(response => {
            const newFormData = populateOptionFields(formdata, this.props.products.woods,'wood')
            this.updateFields(newFormData)
            //console.log(newFormData);
        }).catch( e => console.log(e))
    }

    render() {
        return (
            <UserLayout>
                <div>
                    <h1>Add product</h1>
                    <form onSubmit={(event) => this.submitForm(event)}> 

                        <FormField 
                            id={"name"}
                            formdata = {this.state.formdata.name}
                            change={(element) => this.updateForm(element)}
                        />

                        <FormField 
                            id={"description"}
                            formdata = {this.state.formdata.description}
                            change={(element) => this.updateForm(element)}
                        />

                        <FormField 
                            id={"price"}
                            formdata = {this.state.formdata.price}
                            change={(element) => this.updateForm(element)}
                        />

                        <div className="form_devider"></div>
                        
                        <FormField 
                            id={"brand"}
                            formdata = {this.state.formdata.brand}
                            change={(element) => this.updateForm(element)}
                        />

                        <FormField 
                            id={"shipping"}
                            formdata = {this.state.formdata.shipping}
                            change={(element) => this.updateForm(element)}
                        />

                        <FormField 
                            id={"available"}
                            formdata = {this.state.formdata.available}
                            change={(element) => this.updateForm(element)}
                        />

                        <div className="form_devider"></div>

                        <FormField 
                            id={"wood"}
                            formdata = {this.state.formdata.wood}
                            change={(element) => this.updateForm(element)}
                        />

                        <FormField 
                            id={"frets"}
                            formdata = {this.state.formdata.frets}
                            change={(element) => this.updateForm(element)}
                        />

                        <FormField 
                            id={"publish"}
                            formdata = {this.state.formdata.publish}
                            change={(element) => this.updateForm(element)}
                        />

                        {
                            this.state.formSuccess ?
                                <div className="form_success">
                                    Success..
                                </div>
                            :null
                        }

                        {this.state.formError ? 
                                <div className="error_label">Please check your data</div>
                            :null}
                            <button onClick={ (event) => this.submitForm(event)} >
                                Add Product
                            </button>
                        

                    </form>
                </div>
            </UserLayout>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.products
    }
}

export default connect(mapStateToProps)(AddProduct);
