import React from 'react';
import Card from './card';

const CardBlock = (props) => {
    
    const renderCards = () => (
        props.list ?
            props.list.map((card, i) => (
                
                    <Card 
                        key={i}
                        {...card}
                    />
                
            ))  
        :null
    )
    
    return (
        <div className="card_block">
            <div className="container">
                {
                    props.title ?
                        <div className="title">
                            {props.title}
                        </div>
                    :null
                }
                <div style={{
                    display:'flex',
                    flexWrap: 'wrap'
                }}>
                    { renderCards() }
                </div>
            </div>
        </div>
    );
};

export default CardBlock;
